public class Resize$context.modelElement.name$Feature extends DefaultResizeShapeFeature {

	private $context.modelElement.name$ bo;

	public Resize$context.modelElement.name$Feature(IFeatureProvider fp) {
		super(fp);
		// TODO Auto-generated constructor stub
	}

	@Override
	public boolean canResizeShape(IResizeShapeContext context) {
		Object bo = getBusinessObjectForPictogramElement(context.getPictogramElement());
		return (bo instanceof $context.modelElement.name$ && !(context.getPictogramElement().getGraphicsAlgorithm() instanceof Polygon));
	}

	@Override
	public void resizeShape(final IResizeShapeContext context) {
		TransactionalEditingDomain dom = TransactionUtil.getEditingDomain(getDiagram());
		if (dom == null) 
			dom = TransactionalEditingDomain.Factory.INSTANCE.createEditingDomain(getDiagram().eResource().getResourceSet());
		dom.getCommandStack().execute(new RecordingCommand(dom, "Resize") {

			@Override
			protected void doExecute() {
				bo = ($context.modelElement.name$) getBusinessObjectForPictogramElement(context.getPictogramElement());

				int oWidth = context.getWidth();
				int oHeight = context.getHeight();

				int deltaWidth = context.getShape().getGraphicsAlgorithm().getWidth() - oWidth; 
				int deltaHeight = context.getShape().getGraphicsAlgorithm().getHeight() - oHeight;

				Shape s = context.getShape();
				resizeByDelta(s.getGraphicsAlgorithm(), context.getWidth(), context.getHeight());
				if (s instanceof ContainerShape) {
					for (Shape c : ((ContainerShape) s).getChildren()) {
						resizeChildren(c, deltaWidth, deltaHeight);
					}
				}

				layoutPictogramElement(context.getPictogramElement());
			}

			private void resizeChildren(Shape s, int deltaWidth, int deltaHeight) {
				GraphicsAlgorithm ga = s.getGraphicsAlgorithm();
				int width = ga.getWidth(); 
				int height = ga.getHeight();

				if (s instanceof ContainerShape) {
					if (isResizable(ga))
						resizeByDelta(ga, width - deltaWidth, height - deltaHeight);

					for (Shape c : ((ContainerShape) s).getChildren())
						resizeChildren(c, deltaWidth, deltaHeight);
				}

			}

			private boolean isResizable(GraphicsAlgorithm ga) {
				EObject linkedBO = Graphiti.getLinkService().getBusinessObjectForLinkedPictogramElement(ga.getPictogramElement());
				if (!(ga instanceof AbstractText))
					if (!(Graphiti.getPeService().getProperty(ga, FlowGraphLayoutUtils.KEY_HORIZONTAL).equals(FlowGraphLayoutUtils.KEY_HORIZONTAL_UNDEFINED)))
						if (bo instanceof Container)
							return (linkedBO == null || linkedBO.equals(bo));
				return false;
			}

			private void resizeByDelta(GraphicsAlgorithm ga, int width, int height) {
				Graphiti.getGaService().setLocationAndSize(ga, context.getX(), context.getY(), width, height);

			}
		});

	}

}

