							return ( (linkedBO == null || linkedBO.equals(bo)) && parentIsContainer(ga) );
						if (bo instanceof Node)
							return true;
				}
				return false;
			}

			private boolean parentIsContainer(GraphicsAlgorithm ga) {
				EObject linkedBO = Graphiti.getLinkService().getBusinessObjectForLinkedPictogramElement(ga.getPictogramElement());
				while (linkedBO == null) {
					linkedBO = Graphiti.getLinkService().getBusinessObjectForLinkedPictogramElement((PictogramElement) ga.getPictogramElement().eContainer());
				}
				return (bo.equals(linkedBO));
			}

			private void resizeByDelta(GraphicsAlgorithm ga, int width, int height) {
				Graphiti.getGaService().setLocationAndSize(ga, ga.getX(), ga.getY(), width, height);

			}

			private void resizePolyline(Polyline polyline, IResizeShapeContext context) {
				int oldWidth = polyline.getWidth();
				int oldHeight = polyline.getHeight();
				int newWidth = context.getWidth();
				int newHeight = context.getHeight();

				int contextX = context.getX();
				int contextY = context.getY();
				int deltaX = contextX - polyline.getX();
				int deltaY = contextY - polyline.getY();

				double scaleX = (double) newWidth / (double) oldWidth;
				double scaleY = (double) newHeight / (double) oldHeight;
				scalePoints(polyline.getPoints(), scaleX, scaleY);
				if (polyline.getPictogramElement() instanceof ContainerShape) {
					for (Shape child : ((ContainerShape) polyline.getPictogramElement()).getChildren()) {
						moveByDelta(child, - deltaX, - deltaY);
					}
				}

				Graphiti.getGaService().setLocationAndSize(polyline, contextX, contextY, newWidth, newHeight);
			}

			private void scalePoints(EList<Point> points, double scaleX, double scaleY) {
				for (Point p : points) {
					p.setX((int) (scaleX * p.getX()));
					p.setY((int) (scaleY * p.getY()));
				}
			}

			private boolean isInnerShape(GraphicsAlgorithm ga) {
				if (ga instanceof AbstractText
						|| Graphiti.getPeService().getProperty(ga, $context.graphModelName$LayoutUtils.KEY_HORIZONTAL).equals($context.graphModelName$LayoutUtils.KEY_HORIZONTAL_UNDEFINED))
					return true;
				if (bo instanceof Node)
					return false;
				return !(bo instanceof Container && sharesBusinessObject(ga));
			}

			private boolean sharesBusinessObject(GraphicsAlgorithm ga) {
				EObject linkedBO = Graphiti.getLinkService().getBusinessObjectForLinkedPictogramElement(ga.getPictogramElement());
				while (linkedBO == null) {
					linkedBO = Graphiti.getLinkService().getBusinessObjectForLinkedPictogramElement((PictogramElement) ga.getPictogramElement().eContainer());
				}
				return (bo.equals(linkedBO));
			}

			private void moveByDelta(Shape shape, int deltaX, int deltaY) {
				GraphicsAlgorithm ga = shape.getGraphicsAlgorithm();
				if (ga != null && !isInnerShape(ga)) {
					Graphiti.getGaService().setLocation(ga, ga.getX() + deltaX, ga.getY() + deltaY);
					if (ga instanceof ContainerShape) {
						for (Shape child : ((ContainerShape) ga).getChildren()) {
							moveByDelta(child, deltaX, deltaY);
						}
					}
				}
			}
		});

